function grid_tipos_receitas_selecionar_inclusao_getValue(field, seqRow) {
  seqRow = scSeqNormalize(seqRow);
  var SC_tmp = "";
  if ("descricao" == field) {
    SC_tmp = grid_tipos_receitas_selecionar_inclusao_descricao_getValue(seqRow);
  }
  if ("idtipos_receitas" == field) {
    SC_tmp = grid_tipos_receitas_selecionar_inclusao_idtipos_receitas_getValue(seqRow);
  }
  if ("idgrupos_receita" == field) {
    SC_tmp = grid_tipos_receitas_selecionar_inclusao_idgrupos_receita_getValue(seqRow);
  }
  if ("ativo" == field) {
    SC_tmp = grid_tipos_receitas_selecionar_inclusao_ativo_getValue(seqRow);
  }
  if (typeof(SC_tmp) == 'undefined') {
      SC_tmp = "";
  }
  else {
      SC_tmp = SC_tmp.replace(/[+]/g, "__NM_PLUS__");
      while (SC_tmp.lastIndexOf("&amp;") != -1)
      {
         SC_tmp = SC_tmp.replace("&amp;" , "__NM_AMP__");
      }
      SC_tmp = SC_tmp.replace(/[&]/g, "__NM_AMP__");
      SC_tmp = SC_tmp.replace(/[%]/g, "__NM_PRC__");
  }
  return SC_tmp;
}

